ed = 15;              // Maximum speed (RPM) for the stepper motor\n\nint counter = 0;\nint direction = DIRECTION_CW;\nint CLK_state;\nint prev_CLK_state;\n\nStepper myStepper(stepsPerRevolution, 8, 9, 10, 11);  // Initialize the stepper library on pins 8 through 11\nezButton button(SW_PIN);                              // create ezButton object that attach to pin 4\n\nvoid setup() {\n  Serial.begin(9600);\n\n  // configure encoder pins as inputs\n  pinMode(CLK_PIN, INPUT);\n  pinMode(DT_PIN, INPUT);\n  button.setDebounceTime(50);  // set debounce time to 50 milliseconds\n\n  // read the initial state of the rotary encoder\'s CLK pin\n  prev_CLK_state = digitalRead(CLK_PIN);\n\n  // Set the motor speed\n  myStepper.setSpeed(maxSpeed);\n}\n\nvoid loop() {\n  button.loop();  // MUST call the loop() function first\n\n  // read the current state of the rotary encoder\'s CLK pin\n  CLK_state = digitalRead(CLK_PIN);\n\n  // If the state of CLK is changed, then pulse occurred\n  // React to only the rising edge (from LOW to HIGH) to avoid double count\n  if (CLK_state != prev_CLK_state && CLK_state == HIGH) {\n    // if the DT state is HIGH\n    // the encoder is rotating in counter-clockwise direction => decrease the counter\n    if (digitalRead(DT_PIN) == HIGH) {\n      if (counter > -9) {\n        counter--;\n        direction = DIRECTION_CCW;\n      }\n    } else {\n      // the encoder is rotating in clockwise direction => increase the counter\n      if (counter < 9) {\n        counter++;\n        direction = DIRECTION_CW;\n      }\n    }\n\n    Serial.print("DIRECTION: ");\n    if (direction == DIRECTION_CW)\n      Serial.print("Clockwise");\n    else\n      Serial.print("Counter-clockwise");\n\n    Serial.print(" | COUNTER: ");\n    Serial.println(counter);\n\n    // Control the stepper motor based on the counter value\n    if (direction == DIRECTION_CW) {\n      myStepper.step(stepsPerRevolution / 100);  // Move a small step clockwise\n    } else {\n      myStepper.step(-stepsPerRevolution / 100);  // Move a small step counter-clockwise\n    }\n  }\n\n  // save last CLK state\n  prev_CLK_state = CLK_state;\n\n  if (button.isPressed()) {\n    Serial.println("The button is pressed");\n    counter = 0;            // Reset counter when the button is pressed\n  }\n}\n'})}),"\n",(0,r.jsxs)(n.ol,{start:"3",children:["\n",(0,r.jsxs)(n.li,{children:["\n",(0,r.jsx)(n.p,{children:"Connect your Arduino to your laptop using a USB-C cable and upload the code to the arduino."}),"\n"]}),"\n",(0,r.jsxs)(n.li,{children:["\n",(0,r.jsx)(n.p,{children:"Test! Twist the rotary encoder and press the button! Observe the stepper motors movements and the serial monitor to track the counter."}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.table,{children:[(0,r.jsx)(n.thead,{children:(0,r.jsxs)(n.tr,{children:[(0,r.jsx)(n.th,{children:"Prev"}),(0,r.jsx)(n.th,{children:"Next"})]})}),(0,r.jsx)(n.tbody,{children:(0,r.jsxs)(n.tr,{children:[(0,r.jsx)(n.td,{children:(0,r.jsx)(n.a,{href:"/UNO-R4-Starter-Kit/DC-Motor",children:"26. DC Motor and Relay"})}),(0,r.jsx)(n.td,{children:"N/A"})]})})]})]})}function h(e={}){const{wrapper:n}={...(0,i.R)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(a,{...e})}):a(e)}},28453:(e,n,t)=>{t.d(n,{R:()=>s,x:()=>d});var r=t(96540);const i={},o=r.createContext(i);function s(e){const n=r.useContext(o);return r.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(i):e.components||i:s(e.components),r.createElement(o.Provider,{value:n},e.children)}}}]);